create table cliente (id_cliente int8 generated by default as identity, nome varchar(255), telefone varchar(20), cpf varchar(20), endereco varchar(255), bairro varchar(255), cep varchar(10), estado varchar(100),cidade varchar(255), numero int4, primary key (id_cliente));
create table pedido (id_pedido int8 generated by default as identity, data_postagem date, data_transporte date, data_entrega date, observacao varchar(255), primary key (id_pedido));